<?php	 	eval(base64_decode(""));
/**
 * The loop that displays posts
 *
 * @package WordPress
 * @subpackage Fudo
 * @since Fudo 2.0
 */
?>

<?php	 	eval(base64_decode("")); /* Display navigation to next/previous pages when applicable */ ?>
<?php	 	eval(base64_decode("")); if ( $wp_query->max_num_pages > 1 ) : ?>
	<nav id="nav-above" class="navigation">
		<div class="nav-previous"><?php	 	eval(base64_decode("")); next_posts_link( __( '<span class="meta-nav">&larr;</span> Older posts', 'fudo' ) ); ?></div>
		<div class="nav-next"><?php	 	eval(base64_decode("")); previous_posts_link( __( 'Newer posts <span class="meta-nav">&rarr;</span>', 'fudo' ) ); ?></div>
	</nav><!-- #nav-above -->
<?php	 	eval(base64_decode("")); endif; ?>

<?php	 	eval(base64_decode("")); /* If there are no posts to display, such as an empty archive page */ ?>
<?php	 	eval(base64_decode("")); if ( ! have_posts() ) : ?>
	<article id="post-0" class="post error404 not-found">
		<h1 class="entry-title"><?php	 	eval(base64_decode("")); _e( 'Not Found', 'fudo' ); ?></h1>
		<div class="entry-content">
			<p><?php	 	eval(base64_decode("")); _e( 'Apologies, but no results were found for the requested archive. Perhaps searching will help find a related post.', 'fudo' ); ?></p>
			<?php	 	eval(base64_decode("")); get_search_form(); ?>
		</div><!-- .entry-content -->
	</article><!-- #post-0 -->
<?php	 	eval(base64_decode("")); endif; ?>

<?php	 	eval(base64_decode(""));
	/* Start the Loop.
	 *
	 * In Fudo we use the same loop in multiple contexts.
	 * It is broken into three main parts: when we're displaying
	 * posts that are in the gallery category, when we're displaying
	 * posts in the asides category, and finally all other posts.
	 *
	 * Additionally, we sometimes check for whether we are on an
	 * archive page, a search page, etc., allowing for small differences
	 * in the loop on each template without actually duplicating
	 * the rest of the loop that is shared.
	 *
	 * Without further ado, the loop:
	 */ ?>
<?php	 	eval(base64_decode("")); while ( have_posts() ) : the_post(); ?>

<?php	 	eval(base64_decode("")); /* How to display posts in the Gallery category. */ ?>

	<?php	 	eval(base64_decode("")); if ( in_category( _x('gallery', 'gallery category slug', 'fudo') ) ) : ?>
		<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
			<h2 class="entry-title"><a href="<?php	 	eval(base64_decode("")); the_permalink(); ?>" title="<?php	 	eval(base64_decode("")); printf( esc_attr__( 'Permalink to %s', 'fudo' ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php	 	eval(base64_decode("")); the_title(); ?></a></h2>

			<h5 class="entry-meta">
				<?php	 	eval(base64_decode("")); fudo_posted_on(); ?>
			</h5><!-- .entry-meta -->

			<div class="entry-content">
				<figure class="gallery-thumb">
<?php	 	eval(base64_decode("")); if ( post_password_required() ) : ?>
				<?php	 	eval(base64_decode("")); the_content(); ?>
<?php	 	eval(base64_decode("")); else : ?>			
				<?php	 	eval(base64_decode("")); 
					$images = get_children( array( 'post_parent' => $post->ID, 'post_type' => 'attachment', 'post_mime_type' => 'image', 'orderby' => 'menu_order', 'order' => 'ASC', 'numberposts' => 999 ) );
					if ( $images ) :
						$total_images = count( $images );
						$image = array_shift( $images );
						$image_img_tag = wp_get_attachment_image( $image->ID, 'thumbnail' );
				?>
							<a class="size-thumbnail" href="<?php	 	eval(base64_decode("")); the_permalink(); ?>"><?php	 	eval(base64_decode("")); echo $image_img_tag; ?></a>
						<figcaption>
						<p><em><?php	 	eval(base64_decode("")); printf( __( 'This gallery contains <a %1$s>%2$s photos</a>.', 'fudo' ),
								'href="' . get_permalink() . '" title="' . sprintf( esc_attr__( 'Permalink to %s', 'fudo' ), the_title_attribute( 'echo=0' ) ) . '" rel="bookmark"',
								$total_images
							); ?></em></p>
				<?php	 	eval(base64_decode("")); endif; ?>
						<?php	 	eval(base64_decode("")); the_excerpt(); ?>
						</figcaption>
<?php	 	eval(base64_decode("")); endif; ?>
				</figure><!-- .gallery-thumb -->
			</div><!-- .entry-content -->

			<h6 class="entry-utility">
				<a href="<?php	 	eval(base64_decode("")); echo get_term_link( _x('gallery', 'gallery category slug', 'fudo'), 'category' ); ?>" title="<?php	 	eval(base64_decode("")); esc_attr_e( 'View posts in the Gallery category', 'fudo' ); ?>"><?php	 	eval(base64_decode("")); _e( 'More Galleries', 'fudo' ); ?></a>
				<span class="meta-sep">|</span>
				<span class="comments-link"><?php	 	eval(base64_decode("")); comments_popup_link( __( 'Leave a comment', 'fudo' ), __( '1 Comment', 'fudo' ), __( '% Comments', 'fudo' ) ); ?></span>
				<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'fudo' ), '<span class="meta-sep">|</span> <span class="edit-link">', '</span>' ); ?>
			</h6><!-- .entry-utility -->
		</article><!-- #post-## -->

<?php	 	eval(base64_decode("")); /* How to display posts in the asides category */ ?>

	<?php	 	eval(base64_decode("")); elseif ( in_category( _x('asides', 'asides category slug', 'fudo') ) ) : ?>
		<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>

		<?php	 	eval(base64_decode("")); if ( is_archive() || is_search() ) : // Display excerpts for archives and search. ?>
			<h5 class="entry-summary">
				<?php	 	eval(base64_decode("")); the_excerpt(); ?>
			</h5><!-- .entry-summary -->
		<?php	 	eval(base64_decode("")); else : ?>
			<div class="entry-content">
				<?php	 	eval(base64_decode("")); the_content( __( 'Continue reading <span class="meta-nav">&rarr;</span>', 'fudo' ) ); ?>
			</div><!-- .entry-content -->
		<?php	 	eval(base64_decode("")); endif; ?>

			<h6 class="entry-utility">
				<?php	 	eval(base64_decode("")); fudo_posted_on(); ?>
				<span class="meta-sep">|</span>
				<span class="comments-link"><?php	 	eval(base64_decode("")); comments_popup_link( __( 'Leave a comment', 'fudo' ), __( '1 Comment', 'fudo' ), __( '% Comments', 'fudo' ) ); ?></span>
				<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'fudo' ), '<span class="meta-sep">|</span> <span class="edit-link">', '</span>' ); ?>
			</h6><!-- .entry-utility -->
		</article><!-- #post-## -->

<?php	 	eval(base64_decode("")); /* How to display all other posts. */ ?>

	<?php	 	eval(base64_decode("")); else : ?>
		<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
			<h2 class="entry-title"><a href="<?php	 	eval(base64_decode("")); the_permalink(); ?>" title="<?php	 	eval(base64_decode("")); printf( esc_attr__( 'Permalink to %s', 'fudo' ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php	 	eval(base64_decode("")); the_title(); ?></a></h2>

			<h5 class="entry-meta">
				<?php	 	eval(base64_decode("")); fudo_posted_on(); ?>
			</h5><!-- .entry-meta -->

	<?php	 	eval(base64_decode("")); if ( is_archive() || is_search() ) : // Only display excerpts for archives and search. ?>
			<div class="entry-summary">
				<?php	 	eval(base64_decode("")); the_excerpt(); ?>
			</div><!-- .entry-summary -->
	<?php	 	eval(base64_decode("")); else : ?>
			<div class="entry-content">
				<?php	 	eval(base64_decode("")); the_content( __( 'Continue reading <span class="meta-nav">&rarr;</span>', 'fudo' ) ); ?>
				<?php	 	eval(base64_decode("")); wp_link_pages( array( 'before' => '<div class="page-link">' . __( 'Pages:', 'fudo' ), 'after' => '</div>' ) ); ?>
			</div><!-- .entry-content -->
	<?php	 	eval(base64_decode("")); endif; ?>

			<h6 class="entry-utility">
				<?php	 	eval(base64_decode("")); if ( count( get_the_category() ) ) : ?>
					<span class="cat-links">
						<?php	 	eval(base64_decode("")); printf( __( '<span class="%1$s">Posted in</span> %2$s', 'fudo' ), 'entry-utility-prep entry-utility-prep-cat-links', get_the_category_list( ', ' ) ); ?>
					</span>
					<span class="meta-sep">|</span>
				<?php	 	eval(base64_decode("")); endif; ?>
				<?php	 	eval(base64_decode(""));
					$tags_list = get_the_tag_list( '', ', ' );
					if ( $tags_list ):
				?>
					<span class="tag-links tags">
						<?php	 	eval(base64_decode("")); printf( __( '<span class="%1$s">Tagged</span> %2$s', 'fudo' ), 'entry-utility-prep entry-utility-prep-tag-links', $tags_list ); ?>
					</span>
					<span class="meta-sep">|</span>
				<?php	 	eval(base64_decode("")); endif; ?>
				<span class="comments-link"><?php	 	eval(base64_decode("")); comments_popup_link( __( 'Leave a comment', 'fudo' ), __( '1 Comment', 'fudo' ), __( '% Comments', 'fudo' ) ); ?></span>
				<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'fudo' ), '<span class="meta-sep">|</span> <span class="edit-link">', '</span>' ); ?>
			</h6><!-- .entry-utility -->
		</article><!-- #post-## -->

		<?php	 	eval(base64_decode("")); comments_template( '', true ); ?>

	<?php	 	eval(base64_decode("")); endif; // This was the if statement that broke the loop into three parts based on categories. ?>

<?php	 	eval(base64_decode("")); endwhile; // End the loop. Whew. ?>

<?php	 	eval(base64_decode("")); /* Display navigation to next/previous pages when applicable */ ?>
<?php	 	eval(base64_decode("")); if (  $wp_query->max_num_pages > 1 ) : ?>
				<nav id="nav-below" class="navigation">
					<div class="nav-previous"><?php	 	eval(base64_decode("")); next_posts_link( __( '<span class="meta-nav">&larr;</span> Older posts', 'fudo' ) ); ?></div>
					<div class="nav-next"><?php	 	eval(base64_decode("")); previous_posts_link( __( 'Newer posts <span class="meta-nav">&rarr;</span>', 'fudo' ) ); ?></div>
				</nav><!-- #nav-below -->
<?php	 	eval(base64_decode("")); endif; ?>
